package tests.bo.boClient;

import base.TestBase;
import com.cs.dipocketback.base.data.Site;
import io.restassured.response.Response;
import org.testng.annotations.Test;

import static io.restassured.RestAssured.*;
import static org.hamcrest.CoreMatchers.equalTo;

public class RolesBOUserClientPageReportABugTests extends TestBase {
    String cookie = null;
    int clientId = 34138;
    String firstName = "Pavel";
    String lastName = "auto QA";
    String phone = "380980316499";
    String email = "testdipocket2@gmail.com";
    String image = "";

    @Test(priority = 1)
    public void test_BOServices_v1_auth_authentication(){
        baseURI = app.BOURL;
        basePath = "BOServices";
        cookie = app.getBoRequestsHelper().boServices_v1_auth_authentication(app.BOuserLogin, app.BOuserPass, app.BOusername);
    }

    @Test(priority = 2)
    public void test_BOServices_v1_client_search(){
        app.getBoRequestsHelper().boServices_v1_client_search(cookie, clientId, firstName, lastName, phone, email, Site.SODEXO.toString());
    }

    @Test(priority = 3)
    public void test_BOServices_v1_client_34138(){
        Response res = app.getBoRequestsHelper().boServices_v1_client_(cookie, clientId);
        res.then().body("mainPhone", equalTo(phone),
                "firstName", equalTo(firstName),
                "lastName", equalTo(lastName),
                "email", equalTo(email));
    }

    @Test(priority = 4)
    public void test_BOServices_v1_report_email_sendBugReport(){
        given()
                .spec(app.requestSpecBO)
                .cookie(cookie)
                .body("{\n" +
                        "  \"clientId\" : "+clientId+",\n" +
                        "  \"problem\" : \"test\",\n" +
                        "  \"occurrenceTime\" : \"19.5.2022\",\n" +
                        "  \"occurrenceSide\" : \"Mobile App\",\n" +
                        "  \"attachmentsInBase64\" : \""+image+"\"\n" +
                        "}")
                .when()
                .post( "/v1/report/email/sendBugReport")
                .then().log().all()
                .statusCode(200);
    }
}